import { createContext } from "react";

export const initialState = {
  products: [],
  showLoadingState: true,
  orederedProducts: [],
  changeShowLoadingState: (dispatch) => {
    dispatch({
      type: "CHANGE_LOADING_STATE",
    });
  },
};
export const ProductsContext = createContext();
