import { act } from "@testing-library/react";

export const reducer = (state, action) => {
  switch (action.type) {
    case "CHANGE_LOADING_STATE":
      return {
        ...state,
        showLoadingState: !state.showLoadingState,
      };
    case "SET_PRODUCTS":
      return {
        ...state,
        products: action.products,
      };
      case "ADD_ORDER":
        return
    default:
      return state;
  }
};
