import { act } from "@testing-library/react";

export const reducer = (state, action) => {
  switch (action.type) {
    case "CHANGE_LOADING_STATE":
      return {
        ...state,
        showLoadingState: !state.showLoadingState,
      };
    case "SET_PRODUCTS":
      return {
        ...state,
        products: action.products,
      };
      case "ADD_ORDER":
        const productIndex = state.orederedProducts.find(product=>product.id === action.id)
        const newProducts = {...state.addOrdeerProduct}
        newProducts[productIndex].availableCount = newProducts[productIndex].availableCount-1


        return{
          ...state,
          products:newProducts,
          orederedProducts:addOrdeerProduct(state.orederedProducts,)
        }
    default:
      return state;
  }
};
