import { addOrder } from "../helperFunction/addOrder";


export const reducer = (state, action) => {
  switch (action.type) {
    case "CHANGE_LOADING_STATE":
      return {
        ...state,
        showLoadingState: !state.showLoadingState,
      };
    case "SET_PRODUCTS":
      return {
        ...state,
        products: action.products,
      };
    case "ADD_ORDER":
      const { newProducts, newOrderedProducts, newTotalPrice } = addOrder(
        action.id,
        action.index,
        state.products,
        state.orderedProducts,
        state.totalPrice,
        action.price
      );
